package communications;

/**
 * Class used to generate an authentication response to return to sender
 * 
 * @author aaron bickhaus
 * 
 */
public class AuthenticationResponse {
	/**
	 * class vars
	 */
	private int AuthenticationResponseCode;
	private String AuthenticationErrorDescription;

	/**
	 * default constructor
	 */
	public AuthenticationResponse() {
		super();
		// TODO Auto-generated constructor stub
	}

	/**
	 * constructor
	 * 
	 * @param authenticationResponseCode
	 * @param authenticationErrorDescription
	 */
	public AuthenticationResponse(int authenticationResponseCode,
			String authenticationErrorDescription) {

		AuthenticationResponseCode = authenticationResponseCode;
		AuthenticationErrorDescription = authenticationErrorDescription;
	}

	/**
	 * get response code
	 * 
	 * @return
	 */
	public int getAuthenticationResponseCode() {
		return AuthenticationResponseCode;
	}

	/**
	 * set response code
	 * 
	 * @param authenticationResponseCode
	 */
	public void setAuthenticationResponseCode(int authenticationResponseCode) {
		AuthenticationResponseCode = authenticationResponseCode;
	}

	/**
	 * get error description
	 * 
	 * @return
	 */
	public String getAuthenticationErrorDescription() {
		return AuthenticationErrorDescription;
	}

	/**
	 * message to set error description
	 * 
	 * @param authenticationErrorDescription
	 */
	public void setAuthenticationErrorDescription(
			String authenticationErrorDescription) {
		AuthenticationErrorDescription = authenticationErrorDescription;
	}
}
